function getQueryParam(e){let n=new URLSearchParams(window.location.search);return n.get(e)}function convertToUrl(e){let n=MASTER_SONGS.find(n=>n.id===e);if(n)return`${URL}${n.filename}`}function handleRouting(){let e=getQueryParam("playlist"),n=getQueryParam("songId");e?handlePlaylistRouting(e):n&&handleSingleVideoRouting(n)}function handleSingleVideoRouting(e){if(!MASTER_SONGS||0===MASTER_SONGS.length){console.error("MASTER_SONGS is not loaded or empty.");return}let n=MASTER_SONGS.find(n=>n.id===e);if(!n){console.error(`Song with ID "${e}" not found.`);return}videoPlayer.src=`${URL}${n.filename}`}function handlePlaylistRouting(e){let n=e.split(",").map(e=>convertToUrl(e)).filter(e=>void 0!==e);if(!n||0===n.length){console.error("Playlist is empty or invalid.");return}console.log("Extracted song IDs:",n),videoUrls=n,videoPlayer.src=videoUrls[0]}